import React from "react";
import GalleryStyle from "./Gallery.css"

class Gallery extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      imageNames: this.props.imageNames,
      captions: this.props.captions,
      galleryIndex: 0
    }
  }

  render() {
    let imgUrl = "http://localhost:3001/" + this.props.ImageNames[0];

    function galleryArrow(increment) {
      if(galleryIndex == 0 && increment == -1){
        this.setState(this.props.ImageNames.length);
      }
      else if (galleryIndex == this.props.ImageNames.length && increment == 1){
        this.setState(galleryIndex = 0);
      }
      else {
        this.setState(galleryIndex = galleryIndex + increment);
      }
      imageUrl = "http://localhost:3001/" + this.props.ImageNames[this.state.GalleryIndex];
    }
    return(
      <div id="image_gallery">
        <button onClick={galleryArrow(-1)}> Left</button>

        <button onClick={galleryArrow(1)}>Right</button>
      </div>
    )
  }
}

export default Gallery
