import React from "react";
import logo from './logo.svg';
import './Main.css';
import Grid from '@material-ui/core/Grid';
import AnchorLink from 'react-anchor-link-smooth-scroll';
import Profile from './Profile.js';
import Project from './Project.js';

class Main extends React.Component {
  render() {
    const bioMiguel = "maker autodidacta e investigador del error. \n  Mi trabajo se centra principalmente en el ámbito del arte tecnológico donde intento encontrar un balance entre la investigación y la creación de objetos y/o contenidos no palpables. Mis piezas son fruto de una dualidad en que se equilibra una voluntad por solucionar necesidades objetivas y técnicas, poniéndolas en contraste con visiones e interpretaciones desde una perspectiva subjetiva así como de perspectivas de creadores/as a mi alrededor.";
    const bioSelvIkei = "selv ikei se dedica únicamente al sonido y a las diferentes formas de expresarlo e interpretarlo. Aunque toda la música sea sonido ni todos los sonidos son música y eso algo bien presente en las collage sónicas de este proyecto donde capas y capas de sonidos sintéticos, fríos, apáticos se hunden entre bajos metálicos, redondos y estáticos en un limbo entre lo agradable y lo disonante. Sus paisajes sonoros surgen de una mezcla entre instrumentos electrónicos sintéticos, grabaciones de campo manipuladas e instrumentos electro-acústicos hechos a mano por casulo creando ambientes comúnmente descritos como alusivos a una concepción amarga de la nostalgia.";
    const bioOpaaco= "“opaaco” es un artista visual multimedia autodidacta que a través de sus experimentos erráticos crea entornos visuales orgánicos. Es frecuente encontrarlo moldeando ideas rodeado por objetos que poco tienen que ver entre si. Teles destripadas, desechos de laboratorios obsoletos, quimicos para revelación caducados, lupas, imanes, microscópicos amontonados con las máquinas hechas a medida por casulo. Es el personaje menos técnico de los 3, vivido adepto de la prueba/error y poco apologista de la teoría antes de la práctica. Experimentador nato con una necesidad incontrolable por no hacer las cosas bién. Su naturaleza caótica pero maleable se dá bien en entornos ruidosos con luces poco estáticas.";
    const bioCasulo= "“casulo” es el personaje que se encarga de la idealización, construcción y reparación del hardware de los demás proyectos. Es el personaje más técnico, formal y tradicional de los 3. Es un científico que poco uso recreativo dá a las máquinas que construye, enfocando más su dedicación en la investigación y desarrollo de nuevas formas de interacción hombre-máquina con el objetivo de permitir o simplemente facilitar el proceso creativo de otros artistas/creadorxs, explorando nuevas formas de expresión. Suele darse bien en ambientes silenciosos y bien iluminados.";
    return (
      <div id="page">
      <Grid item xs={12}>
        <Grid container>
            <Grid xs={2} id="menu" item>
              <p><a href="#casulo">casulo</a></p>
              <p><a href="#opaaco">opaaco</a></p>
              <p><a href="#selv_ikei">selv ikei</a></p>
            </Grid>
            <Grid xs={2} item>
              <div id="personas_projects">
              </div>
            </Grid>
            <Grid xs={7} item>
              <Profile
              name="miguel zurk cruz"
              picture="picSelvinho.jpeg"
              bio={bioMiguel}
              />
              <Profile
              name="casulo"
              picture="picCasulo.png"
              bio={bioCasulo}
              />
              <Profile
              name="opaaco"
              picture="picOpaaco.jpg"
              bio={bioOpaaco}
              />
              <Profile
              name="selv ikei"
              picture="picSelvinho.jpeg"
              bio={bioSelvIkei}
              />
            </Grid>
        </Grid>
      </Grid>
      </div>
    );
  }
}

export default Main;
