import React from "react";
import GalleryStyle from "./Gallery.css"

class Gallery extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      imageNames: this.props.imageNames,
      captions: this.props.captions,
      galleryIndex: 0
    }

    this.galleryArrow = this.galleryArrow.bind(this);
  }

  galleryArrow(increment) {
    if(this.state.galleryIndex == 0 && increment == -1){
      this.setState({galleryIndex: this.state.ImageNames.length});
    }
    else if (galleryIndex == this.state.ImageNames.length && increment == 1){
      this.setState({galleryIndex: 0});
    }
    else {
      let newIndex = this.state.galleryIndex + increment;
      this.setState({galleryIndex: newIndex });
    }
    imageUrl = "http://localhost:3001/" + this.state.ImageNames[this.state.GalleryIndex];
  }

  render() {
    let imgUrl = "http://localhost:3001/" + this.state.ImageNames[0];


    return(
      <div id="image_gallery">
        <button onClick={this.galleryArrow(-1)}> Left</button>

        <button onClick={this.galleryArrow(1)}>Right</button>
      </div>
    )
  }
}

export default Gallery
