import React from "react";


class Profile extends React.Component {
  render() {
    let imgSrc = "http://localhost:3001/" + this.props.picture;
    let id = this.props.name.replace(" ", "_");
    return(
      <div id= {id} style={{'margin-top':'20px'}}>
        <img style={{'height':'300px','border-radius':'50%'}} src= {imgSrc} />
        <h1>{this.props.name}</h1>
        <p style={{'text-justify': 'auto'}}>{this.props.bio}</p>
      </div>
    )
  }
}

export default Profile
