import React, { useRef } from "react";
import ProfileStyle from "./Profile.css";
import { useIsVisible } from "react-is-visible";


const Profile = (name, bio, picture, boldChanger) => {
    const nodeRef = useRef();
    const isVisible = useIsVisible(nodeRef);
    let imgSrc = "http://localhost:3001/" + picture;
    let name2 = str(name);
    let id = name2.replace(" ", "_");
    boldChanger(isVisible, name);
    return(
      <div id= {id} class="Project" style={{'margin-top':'30px'}}>
        <img src= {imgSrc} />
        <h1>{name}</h1>
        {bio}
      </div>
    )
}

export default Profile
